mapper.insert:
INSERT INTO t_score(user_name, user_type, sight_name, sight_type, score, time) VALUES (
 #{userName}, #{userType}, #{sightName}, #{sightType},#{score}, #{time}
)

mapper.select:
SELECT * FROM t_score
WHERE
user_name = #{userName}
AND sight_name = #{sightName}

mapper.update:
UPDATE t_score SET
score = #{score},
time = #{time}
WHERE
user_name = #{userName} AND sight_name = #{sightName}
