mapper.insert:
INSERT INTO t_user(user_name, password, phone, type, age, sex) VALUES (
 #{userName}, #{password}, #{phone}, #{type}, #{age}, #{sex}
)

mapper.select:
SELECT id, user_name, password, phone, type, age, sex FROM t_user
WHERE 1=1

    AND type = #{type}


    AND age = #{age}


    AND sex = #{sex}

mapper.update:
UPDATE t_user SET
password = #{password}
WHERE user_name = #{userName}
